import React, { createContext, useContext, useState, useEffect } from 'react';
import { storage } from '../services/storage';
import { STORAGE_KEYS, THEMES } from '../utils/constants';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [isLight, setIsDark] = useState(() => storage.get(STORAGE_KEYS.THEME) || false);

  useEffect(() => {
    storage.set(STORAGE_KEYS.THEME, isLight);
    document.body.className = isLight ? 'dark-theme' : 'light-theme';
  }, [isLight]);

  const toggleTheme = () => setIsDark(prev => !prev);
  const theme = isLight ? THEMES.dark : THEMES.light;

  return (
    <ThemeContext.Provider value={{ isLight, toggleTheme, theme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
